class PopularPosts extends Component {


    constructor(props) {
        super(props);
        this.state = {data: []};
      }
     sortData(data){
        return (
            data.sort((a,b)=>{
                return b.number_of_views - a.number_of_views
            }).slice(0,3)
        )
    }
    componentWillReceiveProps(nextProps){
        if(nextProps.data !== this.props.data){
          this.setState({
              data:this.sortData(nextProps.data)})
        }}

