import React, { Component } from "react";
import { MDBCard, MDBCardBody,MDBCardImage, MDBCol,MDBBtn,MDBNavLink } from "mdbreact";




class SinglePost extends Component {
  state = {
    post:{}
}
componentDidMount(){


        this.setState({
            post: this.props.data
        });

}
render() {
    const post = this.props.data;
  return (
    <MDBCol className="col-md-8 mb-4">

                    <MDBCard className="card mb-4 wow fadeIn">
                    <MDBCardImage className="card-img-top" src={this.state.post.mainimage} alt="Generic placeholder image" />
                        <MDBCardBody className="card-body">

                            <p className="h5 my-4">{this.state.posttitle} </p>
                            <p className="h5 my-4">{this.state.postcategory} {this.state.postpublish_date} </p>
                            <p className="h5 my-4">By {this.state.post.author}</p>
                            <p className="text-justify">{this.state.post.body}</p>
                            <MDBNavLink to={`/post/${this.state.post.slug}`}>
        <MDBBtn rounded outline color="warning">Read More</MDBBtn>
        </MDBNavLink>
                        </MDBCardBody>
                    </MDBCard>

    </MDBCol>
    );
  }
}

export default SinglePost;
